mapper.select:
SELECT A.*, (SELECT (COUNT(*)/ IF(A.trialTotalCount=0, 1, A.trialTotalCount))*100 FROM TrialTb tt3 WHERE tt3.lawFirmId = #{lawFirmId} AND tt3.status = '1' AND tt3.liveType = '1' AND tt3.endingType = '1' ) as "winRate" FROM ( SELECT lft.lawFirmId, lft.name, lft.introduction, lft.profile, lft.status, (SELECT COUNT(*) FROM TrialTb tt WHERE tt.lawFirmId = #{lawFirmId} AND tt.status = '1' AND tt.liveType = '1') AS "trialTotalCount", (SELECT COUNT(*) FROM TrialTb tt2 WHERE tt2.lawFirmId = #{lawFirmId} AND tt2.status = '1')AS "trialCount" FROM LawFirmTb lft LEFT JOIN UserTb ut ON(ut.userId = lft.userId)
lft.lawFirmId = #{lawFirmId} AND lft.status = '1'
) A

SELECT tt.trialId, tt.postType, tt.title, tt.writer, tt.writeDt, tt.`view`, tt.recommendCount, tt.commentCount, null as 'lineType', tt.userId, tt.status, tt.lawFirmId, ut.profile FROM TrialTb tt LEFT JOIN UserTb ut ON(tt.userId = ut.userId)
AND tt.status = 1 AND tt.lawFirmId = #{data.lawFirmId}
AND tt.title LIKE CONCAT('%', #{data.keyword}, '%')
ORDER BY
${order.property} ${order.direction}
OFFSET #{pageable.offset} ROWS FETCH NEXT #{pageable.pageSize} ROWS ONLY

SELECT COUNT(*) FROM TrialTb tt
AND tt.status = 1 AND tt.lawFirmId = #{lawFirmId}
AND tt.title LIKE CONCAT('%', #{keyword}, '%')
